import { tool } from "ai"
import { createTwoFilesPatch } from "diff"
import { promises as fs } from "fs"
import * as path from "path"
import { z } from "zod"
import { toolOutput } from "@/tools/lib/tool-output"
import { trimDiff } from "@/tools/lib/trim-diff"

/**
 * Check if a path is contained within a directory
 */
function isPathWithin(directory: string, filepath: string): boolean {
	const relative = path.relative(directory, filepath)
	return !relative.startsWith("..") && !path.isAbsolute(relative)
}

export const writeTool = tool({
	description: `Writes a file to the local filesystem.

Usage:
- This tool will overwrite the existing file if there is one at the provided path.
- If this is an existing file, you MUST use the Read tool first to read the file's contents. This tool will fail if you did not read the file first.
- ALWAYS prefer editing existing files in the codebase. NEVER write new files unless explicitly required.
- NEVER proactively create documentation files (*.md) or README files. Only create documentation files if explicitly requested by the User.
- Only use emojis if the user explicitly requests it. Avoid writing emojis to files unless asked.
- Writing to paths outside the current working directory will include a warning.`,
	inputSchema: z.object({
		filePath: z
			.string()
			.describe(
				"The absolute path to the file to write (must be absolute, not relative)",
			),
		content: z.string().describe("The content to write to the file"),
	}),
	outputSchema: toolOutput({
		pending: {
			filePath: z.string(),
			result: z.undefined(),
		},
		success: {
			filePath: z.string(),
			result: z.string(),
			content: z.string(),
			lineCount: z.number(),
			byteSize: z.number(),
			wasOverwrite: z.boolean(),
			diff: z.string().optional(),
			warning: z.string().optional(),
		},
		error: {
			filePath: z.string(),
		},
	}),
	toModelOutput: (output) => {
		if (output.status === "error") {
			return {
				type: "error-text",
				value: `Error writing ${output.filePath}: ${output.error}`,
			}
		}
		if (output.status === "success") {
			let result = output.result

			// Add warning if present
			if (output.warning) {
				result = `⚠️ ${output.warning}\n\n${result}`
			}

			// Add diff if overwriting
			if (output.diff && output.wasOverwrite) {
				result += `\n\nChanges:\n${output.diff}`
			}

			return { type: "text", value: result }
		}
		throw new Error("Invalid output status in toModelOutput")
	},
	async *execute({ filePath, content }) {
		const filepath = path.isAbsolute(filePath)
			? filePath
			: path.join(process.cwd(), filePath)

		yield {
			status: "pending",
			message: `Writing file: ${filepath}`,
			filePath: filepath,
			result: undefined,
		}

		try {
			// Check if writing outside working directory
			const cwd = process.cwd()
			let warning: string | undefined
			if (!isPathWithin(cwd, filepath)) {
				warning = `Writing file outside working directory: ${filepath}`
			}

			// Create directory if it doesn't exist
			const dir = path.dirname(filepath)
			await fs.mkdir(dir, { recursive: true })

			// Check if file exists and get its content for diff
			let exists = false
			let previousContent = ""
			try {
				await fs.access(filepath)
				exists = true
				previousContent = await fs.readFile(filepath, "utf-8")
			} catch {
				// File doesn't exist
			}

			// Write the file
			await fs.writeFile(filepath, content, "utf-8")

			// Calculate stats
			const lineCount = content.split("\n").length
			const byteSize = Buffer.byteLength(content, "utf-8")

			// Generate diff if overwriting
			let diff: string | undefined
			if (exists && previousContent !== content) {
				diff = trimDiff(
					createTwoFilesPatch(filepath, filepath, previousContent, content),
				)
			}

			const action = exists ? "overwritten" : "created"
			const message = `File ${action}: ${filepath} (${lineCount} lines, ${byteSize} bytes)`

			yield {
				status: "success",
				message,
				filePath: filepath,
				result: message,
				content,
				lineCount,
				byteSize,
				wasOverwrite: exists,
				diff,
				warning,
			}
		} catch (error) {
			yield {
				status: "error",
				message: `Failed to write ${filepath}`,
				filePath: filepath,
				error: error instanceof Error ? error.message : String(error),
			}
		}
	},
})

import { z } from "zod"

/**
 * Creates a union schema for tool output with pending, success, error, and optional streaming states.
 * This ensures consistent output structure across all tools.
 */
export function toolOutput<
	P extends Record<string, z.ZodTypeAny>,
	S extends Record<string, z.ZodTypeAny>,
	E extends Record<string, z.ZodTypeAny>,
	T extends Record<string, z.ZodTypeAny> | undefined = undefined,
>({
	pending,
	success,
	error,
	streaming,
}: {
	pending: P
	success: S
	error: E
	streaming?: T
}) {
	return z.union([
		z.object({
			status: z.literal("pending"),
			message: z.string(),
			...pending,
		}),
		z.object({
			status: z.literal("success"),
			message: z.string(),
			...success,
		}),
		z.object({
			status: z.literal("error"),
			message: z.string(),
			error: z.string(),
			...error,
		}),
		...(streaming
			? [
					z.object({
						status: z.literal("streaming"),
						message: z.string(),
						...streaming,
					}),
				]
			: []),
	])
}

/**
 * Trim common indentation from diff output for cleaner display.
 * Removes the minimum shared indentation from all diff content lines.
 */
export function trimDiff(diff: string): string {
	const lines = diff.split("\n")
	const contentLines = lines.filter(
		(line) =>
			(line.startsWith("+") || line.startsWith("-") || line.startsWith(" ")) &&
			!line.startsWith("---") &&
			!line.startsWith("+++"),
	)

	if (contentLines.length === 0) return diff

	let min = Infinity
	for (const line of contentLines) {
		const content = line.slice(1)
		if (content.trim().length > 0) {
			const match = content.match(/^(\s*)/)
			if (match) min = Math.min(min, match[1]!.length)
		}
	}

	if (min === Infinity || min === 0) return diff

	const trimmedLines = lines.map((line) => {
		if (
			(line.startsWith("+") || line.startsWith("-") || line.startsWith(" ")) &&
			!line.startsWith("---") &&
			!line.startsWith("+++")
		) {
			const prefix = line[0]
			const content = line.slice(1)
			return prefix + content.slice(min)
		}
		return line
	})

	return trimmedLines.join("\n")
}
